import React, { useEffect, useState } from "react";
import Layout from "../shared/Layout";
import axios from "axios";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { deleteMusic } from "../redux/modules/listSlice";

// 작동 되는것
const List = () => {
  const [musics, setMusics] = useState(null);
  const dispatch = useDispatch();

  useEffect(() => {
    axios.get("http://localhost:3001/lists").then((response) => {
      setMusics(response.data);
    });
  }, []);

  const onDeleteMusic = async (id) => {
    // id.stopPropagation();
    const result = window.confirm("이 할일을 지울까요?");
    if (result) {
      dispatch(deleteMusic(id));
      return window.location.reload();
    } else {
      return;
    }
  };

  return (
    <Layout>
      <div>
        <StGroup>
          {musics?.map((music) => {
            console.log(music.id);
            return (
              <StBox key={music.id}>
                <StHeadBox>
                  <StyledLink to={`/List/${music.id}`} key={music.id}>
                    <div> 상세 페이지 </div>
                  </StyledLink>
                </StHeadBox>
                <div>
                  <li>제목 : "{music.title}"</li>
                  <li>가수 : "{music.singer}"</li>
                  <li>후기 : "{music.desc}"</li>
                </div>
                <button
                  onClick={() => {
                    onDeleteMusic(music.id);
                  }}
                >
                  삭제
                </button>
              </StBox>
            );
          })}
        </StGroup>
        <div></div>
      </div>
    </Layout>
  );
};

export default List;

//CSS

const StBox = styled.div`
  background-image: url(${"data:image/jpeg;base64,"});
  /* opacity: 0.6; */
  color: #f3f3f3;
  border: 8px outset #ff00f2;
  width: 300px;
  height: 150px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: 10px 10px 10px 10px;

  overflow: hidden;
  text-overflow: ellipsis;
  word-break: break-all;
`;

const StGroup = styled.div`
  display: flex;
  justify-content: flex-start;
`;

const StyledLink = styled(Link)`
  /* box-sizing: border-box;
  display: flex;
  padding: 1px 1px; */
  /* padding-left: 13px; */
  text-decoration: none;
  text-align: center;
`;
